import React from "react";

function Math(props) {
    //refer to num1: props.num1
    //refer to num2: props.num2
    //refer to operator: props.operator

    let value; 
    switch (props.operator){
        case "+":
            value= props.num1 + props.num2;
            break;
        case "-":
            value= props.num1 - props.num2;
            break;
        case "*":
            value= props.num1 * props.num2;
            break;
        case "/":
            value= props.num1 / props.num2;
            break;
        default:
            value= NaN;

    }

    return value;
}
